import React, { useState, useEffect, useRef } from 'react';
import EmailPopup from '../components/EmailPopup';
import WeChatQR from '../components/WeChatQR';
import IosAlert from '../components/IosAlert';
import Toast from '../components/Toast';

const HomePage = () => {
  const [showEmailPopup, setShowEmailPopup] = useState(false);
  const [showWeChatQR, setShowWeChatQR] = useState(false);
  const [showIosAlert, setShowIosAlert] = useState(false);
  const [toastMsg, setToastMsg] = useState('');
  const [showToast, setShowToast] = useState(false);
  const [pendingUrl, setPendingUrl] = useState('');
  const contactCardsRef = useRef([]);

  // Âç°ÁâáÂÖ•Âú∫Âä®Áîª
  useEffect(() => {
    const observers = [];
    
    contactCardsRef.current.forEach((card, index) => {
      if (!card) return;
      
      const observer = new IntersectionObserver(
        (entries) => {
          entries.forEach(entry => {
            if (entry.isIntersecting) {
              entry.target.style.animationDelay = `${0.2 + index * 0.2}s`;
              entry.target.classList.add('visible');
              observer.unobserve(entry.target);
            }
          });
        },
        { threshold: 0.2 }
      );
      
      observer.observe(card);
      observers.push(observer);
    });
    
    return () => {
      observers.forEach(observer => observer.disconnect());
    };
  }, []);

  // Â§ÑÁêÜÂ§ñÈÉ®ÈìæÊé•ÂºπÁ™ó
  const handleExternalLink = (url, msg) => {
    setPendingUrl(url);
    setShowIosAlert(true);
  };

  // Á°ÆËÆ§Ë∑≥ËΩ¨
  const confirmIosAlert = () => {
    if (pendingUrl) window.open(pendingUrl, '_blank');
    setShowIosAlert(false);
    setPendingUrl('');
  };

  // Â§çÂà∂ÊàêÂäüÂõûË∞É
  const handleCopySuccess = (email) => {
    setToastMsg(`üìã Â∑≤Â§çÂà∂: ${email}`);
    setShowToast(true);
    setShowEmailPopup(false);
  };

  // ËÅîÁ≥ªÂç°ÁâáÊï∞ÊçÆ
  const contactCards = [
    {
      title: 'ÈÇÆÁÆ±',
      value: 'ÁÇπÂáªÈÄâÊã©ÈÇÆÁÆ±',
      icon: <svg viewBox="0 0 24 24"><path d="M20 4H4v16h16V4zm0 4-8 5-8-5"/></svg>,
      onClick: () => setShowEmailPopup(true)
    },
    {
      title: 'QQ',
      value: '2668588966',
      icon: <img src="/source/ico/favicon_qq.ico" alt="QQ"/>,
      onClick: () => handleExternalLink('https://qm.qq.com/q/WTOl1Swrqa', 'ÊòØÂê¶Ë∑≥ËΩ¨Âà∞ QQÔºü')
    },
    {
      title: 'ÂæÆ‰ø°',
      value: 'Êâ´Á†ÅÂä†Êàë',
      icon: <img src="/source/ico/favicon_wechat.ico" alt="WeChat"/>,
      onClick: () => setShowWeChatQR(true)
    },
    {
      title: 'ÂìîÂì©ÂìîÂì©',
      value: 'bilibili.com',
      icon: <img src="/source/ico/favicon_bilibili.ico" alt="Bilibili"/>,
      onClick: () => handleExternalLink('https://space.bilibili.com/1502522588', 'ÊòØÂê¶Ë∑≥ËΩ¨Âà∞ ÂìîÂì©ÂìîÂì©Ôºü')
    },
    {
      title: 'ÈÖ∑ÂÆâ',
      value: 'coolapk.com',
      icon: <img src="/source/ico/favicon_coolapk.ico" alt="Coolapk"/>,
      onClick: () => handleExternalLink('http://www.coolapk.com/u/3561588', 'ÊòØÂê¶Ë∑≥ËΩ¨Âà∞ ÈÖ∑ÂÆâÔºü')
    },
    {
      title: 'ÂæÆÂçö',
      value: 'weibo.com',
      icon: <img src="/source/ico/favicon_weibo.ico" alt="Weibo"/>,
      onClick: () => handleExternalLink('https://weibo.com/u/7452701895', 'ÊòØÂê¶Ë∑≥ËΩ¨Âà∞ ÂæÆÂçöÔºü')
    },
    {
      title: 'Â∞èÁ∫¢‰π¶',
      value: 'xiaohongshu.com',
      icon: <img src="/source/ico/favicon_xiaohongshu.ico" alt="Xiaohongshu"/>,
      onClick: () => handleExternalLink('https://www.xiaohongshu.com/user/profile/5e950c600000000001009b54', 'ÊòØÂê¶Ë∑≥ËΩ¨Âà∞ Â∞èÁ∫¢‰π¶Ôºü')
    },
    {
      title: 'Áü•‰πé',
      value: 'zhihu.com',
      icon: <img src="/source/ico/favicon_zhihu.ico" alt="Zhihu"/>,
      onClick: () => handleExternalLink('https://www.zhihu.com/people/31-87-23-61', 'ÊòØÂê¶Ë∑≥ËΩ¨Âà∞ Áü•‰πéÔºü')
    }
  ];

  return (
    <main className="page index-page">
      <header>
        <div className="title-row">
          <h1>ÁºéÈáë</h1>
          <picture>
            <source srcset="/source/png/SatinAu_v3_White.png" media="(prefers-color-scheme: dark)"/>
            <img src="/source/png/SatinAu_v3_Black.png" alt="SatinAu Logo"/>
          </picture>
        </div>
      </header>

      <section>
        <p>‰øÉÊàêÂ§ßËá™Áî±‰∏≠‰πãÁ§æ‰ºöËøõÊ≠•ÂèäËæÉÂñÑ‰πãÊ∞ëÁîü</p>
        <p>To promote social progress and better standards of life in larger freedom</p>
      </section>

      <section>
        <h2>ËÅîÁ≥ªÊñπÂºè / Contact Information</h2>
        <div className="contact-wrapper">
          {contactCards.map((card, idx) => (
            <div
              key={idx}
              ref={el => contactCardsRef.current[idx] = el}
              className="contact-card"
              onClick={card.onClick}
            >
              <div className="icon">{card.icon}</div>
              <div className="text">
                <div className="label">{card.title}</div>
                <div className="value">{card.value}</div>
              </div>
            </div>
          ))}
        </div>
      </section>

      <footer>
        <p>
          ¬© 2025 ÁºéÈáëSatinAu ‰øùÁïôÊâÄÊúâÊùÉÂà©„ÄÇ 
          <a href="https://www.kdocs.cn/l/chC750lu9LQC" target="_blank" rel="noopener noreferrer">ÈóÆÈ¢òÂèçÈ¶à</a>
        </p>
      </footer>

      {/* ÂºπÁ™óÁªÑ‰ª∂ */}
      <EmailPopup 
        show={showEmailPopup} 
        onClose={() => setShowEmailPopup(false)}
        onCopySuccess={handleCopySuccess}
      />
      <WeChatQR 
        show={showWeChatQR} 
        onClose={() => setShowWeChatQR(false)}
      />
      <IosAlert 
        show={showIosAlert} 
        message="ÊòØÂê¶Ë∑≥ËΩ¨Âà∞Â§ñÈÉ®ÈìæÊé•Ôºü"
        onConfirm={confirmIosAlert}
        onCancel={() => setShowIosAlert(false)}
      />
      <Toast show={showToast} message={toastMsg} />
    </main>
  );
};

export default HomePage;
