import React, { useEffect, useRef } from 'react';

const ZelynnPage = () => {
  const langCardsRef = useRef([]);

  // Âç°ÁâáÂÖ•Âú∫Âä®Áîª
  useEffect(() => {
    const observers = [];
    
    langCardsRef.current.forEach((card, index) => {
      if (!card) return;
      
      const observer = new IntersectionObserver(
        (entries) => {
          entries.forEach(entry => {
            if (entry.isIntersecting) {
              entry.target.style.animationDelay = `${0.2 + index * 0.2}s`;
              entry.target.classList.add('visible');
              observer.unobserve(entry.target);
            }
          });
        },
        { threshold: 0.2 }
      );
      
      observer.observe(card);
      observers.push(observer);
    });
    
    return () => {
      observers.forEach(observer => observer.disconnect());
    };
  }, []);

  // ËØ≠Ë®ÄÂç°ÁâáÊï∞ÊçÆ
  const langCards = [
    {
      title: 'ËØ≠Ë®Ä',
      value: 'ÁÆÄ‰Ωì‰∏≠Êñá',
      icon: <span className="paw-pink">üêæ</span>,
      url: 'https://www.kdocs.cn/l/cbnmJFr498XG'
    },
    {
      title: 'Ë™ûË®Ä',
      value: 'ÁπÅÈ´î‰∏≠Êñá',
      icon: <span className="paw-purple">üêæ</span>,
      url: 'https://www.kdocs.cn/l/caB01lLIj8q2'
    },
    {
      title: 'Language',
      value: 'English',
      icon: <span className="paw-cyan">üêæ</span>,
      url: 'https://www.kdocs.cn/l/cjSN22oChlaT'
    }
  ];

  return (
    <main className="page zelynn-page">
      <header>
        <h1>Ê≥ΩÂáåZelynn</h1>
      </header>

      <section>
        <h2>ËÆæÂÆöÊÄªËßà / Character Overview</h2>
        <div className="lang-wrapper">
          {langCards.map((card, idx) => (
            <a
              key={idx}
              ref={el => langCardsRef.current[idx] = el}
              className="contact-card"
              href={card.url}
              target="_blank"
              rel="noopener noreferrer"
            >
              <div className="icon">{card.icon}</div>
              <div className="text">
                <div className="label">{card.title}</div>
                <div className="value">{card.value}</div>
              </div>
            </a>
          ))}
        </div>
      </section>

      <footer>
        <p>
          ¬© 2025 ÁºéÈáëSatinAu ‰øùÁïôÊâÄÊúâÊùÉÂà©„ÄÇ 
